/* eslint-disable no-template-curly-in-string */
import CustomForm from "./customForm";
import CustomButton from "./addButtonGrey";
import { useState } from "react";

function CustomName({name, type, placeholder}) {
  const [orderList, setOrderList] = useState([]);

  const addOrderHadler = event => {
    setOrderList(orderList.concat(<CustomForm type={type} placeholder={placeholder} onPress={{}} key={orderList.length}/>))
  }

  const deleteOrderHandler = () => {}

  return (
    <div className='w-[80%] mx-auto'>
      <div className="flex flex-row justify-between mb-[14px]">
        <p className='text-[18px] text-left text-rose-500 text-inter'>{name}</p>
        <p className='text-[18px] text-left text-rose-500 text-inter'>Total</p>
      </div>
      <CustomForm type={type} placeholder={placeholder} onPress={{}}/>
      {orderList}
      <div className="h-[22px]"></div>
      <CustomButton onClick={addOrderHadler}/>
    </div>
  );
}

  export default CustomName;
